import { useState } from "react";
import { nanoid } from "nanoid";
import NotesList from "./components/NotesList";

const App = () => {
  const [notes, setNotes] = useState([
    {
      id: nanoid(),
      text: "This is my first note",
      date: "17/10/2023",
    },
    {
      id: nanoid(),
      text: "This is my 2nd note",
      date: "17/10/2023",
    },
    {
      id: nanoid(),
      text: "This is my 3rd note",
      date: "17/10/2023",
    },
    {
      id: nanoid(),
      text: "This is my fourth note",
      date: "17/10/2023",
    },
    {
      id: nanoid(),
      text: "This is my 5th note",
      date: "17/10/2023",
    },
  ]);

  const addNote = (text) => {
    const date = new Date();
    const newNote = {
      id: nanoid,
      text: text,
      date: date.ToLocalDateString(),
    };
  };
  return (
    <div className="container">
      <NotesList notes={notes} addNoteFunction={addNote} />
    </div>
  );
};

export default App;
